<?phpnamespace DefenderService\DamMicroService;use Illuminate\Support\ServiceProvider;use Laravel\Lumen\Application as LumenApplication;class DamMicroServiceProvider extends ServiceProvider{    public function boot()    {        if($this->app instanceof LumenApplication){            $this->app->configure('services');        }else            $this->publishes([                dirname(__DIR__).'/config/services.php' => config_path('services.php'), ],                'dam-services'            );    }    public function register()    {        $this->app->bind('Service', function(){            return $this->app->make('DefenderService\DamMicroService\Service');        });    }}