<?phpnamespace DefenderService\DamMicroService\Queue;use Illuminate\Queue\Events\JobProcessed;use Mockery\Exception;use Monolog\Logger;use Monolog\Handler\StreamHandler;class QueueManager{    static $workerTimes = 0;    static $maxTimes;    /**     * Execute the job.     * create by changsheng.gu@vcg.com     * @return void     */    public static function MaxWorkTimes($event)    {        self::$maxTimes  = config('damQueueManager.workerMaxTimes')??500;        if($event instanceof JobProcessed){            self::$workerTimes++;        }else{            if(self::$workerTimes >=self::$maxTimes)            {                posix_kill(posix_getpid(), SIGTERM);            }        }    }}