<?phpnamespace DefenderService\DamMicroService\Services\Resource;use DefenderService\DamMicroService\Contracts\MicroServiceApplicationInterface;use DefenderService\DamMicroService\Supports\traits;class GroupApi {    protected $config;    protected $token;    use traits;    public function __construct($config,$token){        $this->config   = $config;        $this->token    =  $token;    }    public function update($data){       return  $this->PostJson($this->config->get('update'),$data,$this->token);    }    public function delete($data){        return  $this->PostJson($this->config->get('delete'),$data,$this->token);    }    public function Merge($data){        return  $this->PostJson($this->config->get('Merge'),$data,$this->token);    }    public function groupInfo(){    }}